import React, { useState, useEffect } from "react";
import Arweave from "arweave";
import styles from "./App.module.css";

const arweave = Arweave.init({
  host: "localhost",
  port: 1984,
  protocol: "http",
  timeout: 20000,
  logging: true,
});

function App() {
  const [records, setRecords] = useState([]);
  const [course, setCourse] = useState("");
  const [recommendation, setRecommendation] = useState("No recommendation yet");
  const [wallet, setWallet] = useState(null); // Userâ€™s wallet
  const [walletAddress, setWalletAddress] = useState("");
  const [walletFile, setWalletFile] = useState(null);

  // Load wallet address after login
  useEffect(() => {
    if (wallet) {
      const getAddress = async () => {
        const address = await arweave.wallets.jwkToAddress(wallet);
        setWalletAddress(address);
      };
      getAddress();
    }
  }, [wallet]);

  // Fetch records for the logged-in wallet
  useEffect(() => {
    const loadRecords = async () => {
      if (!walletAddress) return; // Wait for login
      try {
        console.log("Fetching records for:", walletAddress);
        const txIds = await arweave.arql({
          op: "and",
          expr1: { op: "equals", expr1: "App-Name", expr2: "PermaLearn" },
          expr2: { op: "equals", expr1: "User-Address", expr2: walletAddress },
        });
        const fetchedRecords = await Promise.all(
          txIds.map(async (txId) => {
            const tx = await arweave.transactions.get(txId);
            const data = JSON.parse(tx.get("data", { decode: true, string: true }));
            return { ...data, txId };
          })
        );
        setRecords(fetchedRecords);
        console.log("Records loaded:", fetchedRecords);
      } catch (error) {
        console.error("Error loading records:", error);
      }
    };
    loadRecords();
  }, [walletAddress]);

  // Recommendation logic
  const mockAORecommend = () => {
    if (records.length === 0) return "No recommendation yet";
    const lastCourse = records[records.length - 1].name;
    if (lastCourse.includes("HTML")) return "Try CSS Basics next!";
    if (lastCourse.includes("CSS")) return "Try JavaScript Basics next!";
    if (lastCourse.includes("JavaScript")) return "Try React Basics next!";
    if (lastCourse.includes("React")) return "Try Node.js Basics next!";
    return "Keep exploring advanced topics!";
  };

  useEffect(() => {
    setRecommendation(mockAORecommend());
  }, [records]);

  // Handle wallet file upload
  const handleWalletUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        try {
          const walletData = JSON.parse(event.target.result);
          setWallet(walletData);
          setWalletFile(file);
          console.log("Wallet loaded successfully");
        } catch (error) {
          alert("Invalid wallet file!");
          console.error("Wallet parse error:", error);
        }
      };
      reader.readAsText(file);
    }
  };

  // Add a record with the logged-in wallet
  const addRecord = async () => {
    if (!wallet) {
      alert("Please sign in with your wallet first!");
      return;
    }
    if (!course.trim()) {
      alert("Please enter a course name!");
      return;
    }

    const newRecord = {
      name: course.trim(),
      date: new Date().toLocaleString(),
    };

    try {
      console.log("Creating transaction...");
      const transaction = await arweave.createTransaction(
        { data: JSON.stringify(newRecord) },
        wallet
      );
      transaction.addTag("App-Name", "PermaLearn");
      transaction.addTag("Content-Type", "application/json");
      transaction.addTag("User-Address", walletAddress);

      console.log("Signing transaction...");
      await arweave.transactions.sign(transaction, wallet);

      console.log("Posting transaction to Arlocal...");
      const response = await arweave.transactions.post(transaction);

      console.log("Response from Arlocal:", response.status, response.statusText);
      if (response.status === 200) {
        setRecords((prevRecords) => [
          ...prevRecords,
          { ...newRecord, txId: transaction.id },
        ]);
        setCourse("");
        console.log("Record saved! Transaction ID:", transaction.id);
      } else {
        alert(`Failed to save to Arweave. Status: ${response.status} (${response.statusText})`);
      }
    } catch (error) {
      console.error("Error saving to Arweave:", error);
      alert("Something went wrong. Check the console for details.");
    }
  };

  // Sign out
  const signOut = () => {
    setWallet(null);
    setWalletAddress("");
    setRecords([]);
    setWalletFile(null);
    console.log("Signed out");
  };

  return (
    <div className={styles.app}>
      <h1 className={styles.header}>PermaLearn</h1>

      {!wallet ? (
        <div className={styles.loginContainer}>
          <h2>Sign In / Sign Up</h2>
          <p>Upload your Arweave wallet file to start tracking your achievements.</p>
          <input
            type="file"
            accept=".json"
            onChange={handleWalletUpload}
            className={styles.fileInput}
          />
        </div>
      ) : (
        <>
          <div className={styles.userInfo}>
            <p>Logged in as: {walletAddress.slice(0, 6)}...{walletAddress.slice(-4)}</p>
            <button onClick={signOut} className={styles.signOutButton}>
              Sign Out
            </button>
          </div>

          <div className={styles.formContainer}>
            <input
              type="text"
              value={course}
              onChange={(e) => setCourse(e.target.value)}
              placeholder="Enter course name"
              className={styles.input}
            />
            <button onClick={addRecord} className={styles.button}>
              Add
            </button>
          </div>

          <div className={styles.recordsContainer}>
            <h2 className={styles.subHeader}>Your Learning Records</h2>
            {records.length === 0 ? (
              <p className={styles.emptyText}>No records yet. Start learning!</p>
            ) : (
              <ul className={styles.recordsList}>
                {records.map((record, index) => (
                  <li key={index} className={styles.recordItem}>
                    <span className={styles.recordName}>{record.name}</span>
                    <div>
                      <span className={styles.recordDate}>{record.date}</span>
                      {record.txId && (
                        <a
                          href={`http://localhost:1984/${record.txId}`}
                          target="_blank"
                          rel="noopener noreferrer"
                          className={styles.txLink}
                        >
                          View Locally
                        </a>
                      )}
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>

          <div className={styles.recommendationsContainer}>
            <h2 className={styles.subHeader}>Recommendations</h2>
            <p className={styles.emptyText}>{recommendation}</p>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
